mapper.insert:

	SELECT NVL(MAX(NO), 0)+1 FROM DETAIL

  INSERT INTO DETAIL(
                SPOT_NO
              , NO
              , DAY_VISIT
              , PLAN_NO
              , CATEGORY_ID
              , DAY_NO
              , SPOT_NAME
              , SPOT_FURL
              , LAT
              , LNG
              , CITY_NAME)
  VALUES(
       #{spotNo}
     , #{no} 
     , #{dayVisit}
     , #{planNo}
     , #{categoryId}
     , #{dayNo}
     , #{spotName}
     , #{spotFurl}
     , #{lat}
     , #{lng}
     , #{cityName})
