mapper.insert:

	SELECT NVL(MAX(NO), 0)+1 FROM DETAIL

  INSERT INTO DETAIL(
                SPOT_NO
              , NO
              , DAY_VISIT
              , PLAN_NO
              , CATEGORY_ID
              , DAY_NO
              , SPOT_NAME
              , SPOT_FURL
              , LAT
              , LNG
              , CITY_NAME)
  VALUES(
       #{spotNo}
     , #{no} 
     , #{dayVisit}
     , #{planNo}
     , #{categoryId}
     , #{dayNo}
     , #{spotName}
     , #{spotFurl}
     , #{lat}
     , #{lng}
     , #{cityName})

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM (
SELECT P.PLAN_NO , P.BLOG_TITLE , M.MEMBER_NICK , D.SPOT_FURL , D.CITY_NAME , PLAN_START , TO_CHAR(PLAN_START,'YYYY"년"MM"월"DD"일"') AS PLAN_STARTS
FROM PLAN P, MEMBER M, DETAIL D WHERE M.MEMBER_NO = P.MEMBER_NO AND D.PLAN_NO = P.PLAN_NO AND D.DAY_NO=1 AND D.DAY_VISIT=1 ORDER BY PLAN_NO DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM > #{firstIndex}
